import React, { Component } from "react";
import axios from "axios";
import SuperMarketList from "./Components/SupermarketList.component";

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            username: "admin",
            password: "admin",
            logedin: false,

            superMarkets: [],
            token: "",
        };

        //Bindig
        this.authenticate = this.authenticate.bind(this);
        this.getallSuperMarkets = this.getallSuperMarkets.bind(this);

        //Axios Setup
        this.authenticate = this.authenticate.bind();
        axios.defaults.baseURL = "https://localhost:8443/";
        axios.defaults.headers.common["Content-Type"] =
            "application/json;charset=utf-8";
    }

    authenticate() {
        if (this.state.logedin) return;

        let body = {
            username: this.state.username,
            password: this.state.password,
        };

        axios
            .post("/login", body)
            .then((response) => {
                this.setState({ token: response.data.token });
            })
            .catch((err) => console.log(err));
    }

    getallSuperMarkets() {
        axios
            .get("/supermarkets")
            .then((response) => this.setState({ superMarkets: response.data }))
            .catch((err) => console.log(err));
    }

    render() {
        return (
            <div className="App">
                <button onClick={this.authenticate}>It hurts</button>
                <button onClick={this.getallSuperMarkets}>
                    But this hurts more
                </button>

                <SuperMarketList superMarkets={this.state.superMarkets} />
            </div>
        );
    }
}

export default App;
