class JwtClient {
    token = "";

    getToken() {
        return this.token;
    }

    async authenticate(username, password) {
        var body = {
            username: username,
            password: password,
        };

        var myHeaders = new Headers({
            "Content-Type": "application/json;charset=utf-8",
        });

        let response = await fetch("https://localhost:8443/login", {
            method: "POST",
            headers: myHeaders,
            mode: "cors",
            cache: "default",
            body: JSON.stringify(body),
        });

        this.token = JSON.parse(await response.json());
    }
}

export default JwtClient;
