import React, { Component } from "react";
import JwtControler from "./Controlers/Jwt.controler.js";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Home from "./Components/Home.component.jsx";

class App extends Component {
    constructor(props) {
        this.state = {
            jwtControler: new JwtControler(),
            token: "no token",
        };
    }

    state = {
        jwtControler: new JwtControler(),
        token: "no token",
    };
    render() {
        return (
            <Router>
                <div className="App">
                    <label>{this.state.token}</label>

                    <button
                        onClick={() => {
                            this.state.jwtControler
                                .authenticate("admin", "admin")
                                .then(
                                    this.setState({
                                        token: this.state.jwtControler.getToken(),
                                    })
                                );
                        }}
                    >
                        It hurts
                    </button>
                </div>
                <Link to="/login"> login </Link>
                <Route
                    path="/login"
                    exact
                    component={() => (
                        <Home jwtControler={this.state.jwtControler} />
                    )}
                />
            </Router>
        );
    }
}

export default App;
