import React, { Component } from "react";
import axios from "axios";
import SuperMarketList from "./Components/SupermarketList.component";

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            username: "admin",
            password: "admin",
            logedin: false,

            superMarkets: [],
            token: "",
        };

        //Bindig
        this.authenticate = this.authenticate.bind(this);
        this.onUserNameChange = this.onUserNameChange.bind(this);
        this.onUserPassChange = this.onUserPassChange.bind(this);

        //Axios Setup
        axios.defaults.baseURL = "https://localhost:8443/";
        axios.defaults.headers.post["Content-Type"] =
            "application/json;charset=utf-8";
    }

    authenticate() {
        if (this.state.logedin) return;

        let body = {
            username: this.state.username,
            password: this.state.password,
        };

        axios
            .post("https://localhost:8443/login", body)
            .then((response) => {
                this.setState({ token: response.data.token, logedin: true });
            })
            .catch((err) => console.log(err));
    }

    //Login Form Methods
    onUserNameChange(event) {
        this.setState({ username: event.target.value });
    }

    onUserPassChange(event) {
        this.setState({ password: event.target.value });
    }

    onSubmit(event) {
        //this.authenticate();
        event.preventDefault();
    }

    //Render
    render() {
        return (
            <div className="App">
                {this.state.logedin ? <label>loged</label> : <div></div>}

                <SuperMarketList />
            </div>
        );
    }
}

export default App;
