import React, { Component } from "react";
import axios from "axios";
import SuperMarketList from "./Components/SupermarketList.component";

class App extends Component {
    constructor(props) {
        super(props);

        this.state = {
            token: "no token",
            username: "admin",
            password: "admin",
            logedin: false,

            superMarkets: [],
            logedaxios: null,
        };

        //Bindig
        this.authenticate = this.authenticate.bind(this);
        this.getallSuperMarkets = this.getallSuperMarkets.bind(this);

        //Axios Setup
        this.authenticate = this.authenticate.bind();
        axios.defaults.baseURL = "https://localhost:8443/";
        axios.defaults.headers.post["Content-Type"] =
            "application/json;charset=utf-8";
    }

    authenticate() {
        if (this.state.logedin || this.state.token != "") return;

        let body = {
            username: this.state.username,
            password: this.state.password,
        };

        axios
            .post("/login", body)
            .then((response) =>
                this.setState({
                    logedin: true,
                    token: response.data.token,
                    logedaxios: axios.create({}),
                })
            )
            .catch((err) => console.log(err));
    }

    getallSuperMarkets() {
        var token = `Bearer ${this.state.token}`;
        console.log(token);
        axios
            .get("/supermarkets", {
                headers: { Authorization: token },
            })
            .then((response) => this.setState({ superMarkets: response.data }))
            .catch((err) => console.log(err));
    }

    render() {
        return (
            <div className="App">
                <button onClick={this.authenticate}>It hurts</button>
                <button onClick={this.getallSuperMarkets}>
                    But this hurts more
                </button>

                <SuperMarketList superMarkets={this.state.superMarkets} />
            </div>
        );
    }
}

export default App;
